import React from "react";
import { View, Text, TouchableOpacity, StyleSheet } from "react-native";
// export function Nav () {
//     return (
//         <View>
//             <Text>Hello</Text>
//         </View>
//     )
// }
export class VerticalBarButton extends React.Component {
    constructor(props) {
        super(props);

        this.allBtNames = ['bt1','bt2'];
        
    }
    
render (){
    return (
        <View style={styles.verticalBar}>
            {/* <TouchableOpacity
                style={styles.button}
                key={1}
                onPress={() => alert(`click on ${1}!`)}>
                <Text style={styles.textButton}>1</Text>
              </TouchableOpacity> */}
            {this.allBtNames.map((name) => {
                <TouchableOpacity
                style={styles.button}
                key={name}
                onPress={() => alert(`click on ${name}!`)}>
                <Text style={styles.textButton}>{name}</Text>
              </TouchableOpacity>
            })}
          
        </View>
      );
 }
  
}

const styles = StyleSheet.create({
  verticalBar: {
    // flex: 1,
    // flexDirection: "row",
  },
  button: {
    width: 50,
    height: 50,
    backgroundColor: "#B8860B",
    color: "#FFF",
    padding: 3,
  },
  textButton: {
    color: "#FFF",
  },
});
